import React, { useState } from 'react';

const App =() =>{
    const purple='#8e44ad';
    const[bg,changebg]=useState('yellow');

    const[name, setName]=useState('Click Me');

    const bgchange =() =>{
        changebg('#8e44ad'); 
        setName('Hi');   
    };
    return(
        <>
            <div style={{ backgroundColor : bg }}> 
            <button onClick={bgchange}> {name} </button>
            </div>

        </>
    );
}
export default App;
