import React, { useState } from 'react';

const App =() =>{
    let delay=60;
    let prevent=false;
    const purple='#8e44ad';
    const[bg,changebg]=useState('yellow');
    const[name, setName]=useState('Click Me');

    const bgchange =() =>{
        changebg('#8e44ad'); 
        setName('Hi ');
        setTimeout(() => {
            prevent = false;
          }, delay);   
    };

    const bgchange2 =() =>{
        changebg(purple); 
        setName('Ouch ');   
    };

    return(
        <>
            <div style={{ backgroundColor : bg }}> 
            <button onClick={bgchange} 
            onDoubleClick={bgchange2}> {name} </button>
            </div>
        </>
    );
}
export default App;
